import React, { useState } from "react";
import axios from "axios";

import "./LandingPage/css/animate.css";
import "./LandingPage/css/bootstrap.min.css";
import "./LandingPage/css/default.css";
import "./LandingPage/css/lineicons.css";
import "./LandingPage/css/style.css";
import "./LandingPage/css/style.css.map";
import Card from "@mui/material/Card";

const Upload = () => {
  const [file, setFile] = useState("");
  const [array, setArray] = useState([]);

  const fileReader = new FileReader();

  const handleOnChange = (e) => {
    setFile(e.target.files[0]);
  };

  const csvFileToArray = (string) => {
    const csvHeader = string.slice(0, string.indexOf("\n")).split(",");
    const csvRows = string.slice(string.indexOf("\n") + 1).split("\n");

    const array = csvRows.map((i) => {
      const values = i.split(",");
      const obj = csvHeader.reduce((object, header, index) => {
        object[header] = values[index];
        return object;
      }, {});
      return obj;
    });

    setArray(array);
  };
  const headerKeys = Object.keys(Object.assign({}, ...array));

  const handleOnSubmit = (e) => {
    e.preventDefault();

    if (file) {
      fileReader.onload = function (event) {
        const text = event.target.result;
        csvFileToArray(text);
      };
      fileReader.readAsText(file);
    }

    const formData = new FormData();
    formData.append("file", file);
    const headers = {
      "Content-Type": "multipart/form-data",
    };
    console.log(formData);

    fetch("http://localhost:5000/upload", {
      method: "POST",
      body: formData,
    })
      .then((res) => console.log(res))
      .catch((err) => console.warn(err));
  };

  return (
    <div>
      <div>
        <div className="header-shape-1"></div>
        <header className="header-area ">
          <div
            id="home"
            className="header-hero bg_cover d-lg-flex align-items-center"
          >
            <div className="shape shape-1"></div>
            <div className="shape shape-2"></div>
            <div className="shape shape-3"></div>
            <div className="shape shape-4"></div>
            <div className="shape shape-5"></div>
            <div className="shape shape-6"></div>

            <div className="container">
              <div className="row align-items-center justify-content-center justify-content-lg-between">
                <div className="col-lg-6 col-md-10"></div>

                <Card variant="outlined">
                  <form>
                    <input
                      type={"file"}
                      id={"csvFileInput"}
                      accept={".csv"}
                      onChange={handleOnChange}
                    />

                    <button
                      onClick={(e) => {
                        handleOnSubmit(e);
                      }}
                    >
                      IMPORT CSV
                    </button>
                  </form>
                </Card>

                <br />

                <table>
                  <thead>
                    <tr key={"header"}>
                      {headerKeys.map((key) => (
                        <th>{key}</th>
                      ))}
                    </tr>
                  </thead>

                  <tbody>
                    {array.map((item) => (
                      <tr key={item.id}>
                        {/* {send(item)} */}
                        {Object.values(item).map((val) => (
                          <td>{val}</td>
                        ))}
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </header>
      </div>
    </div>
  );
};

export default Upload;
